import React, { useState } from "react";
import "./Campaigns.css";

export const Campaigns = () => {
  const [showModal, setShowModal] = useState(false);
  const [campaignData, setCampaignData] = useState([
    {
      id: "1",
      name: "camp1",
      platform: "plat1",
      landingPage: "LP1",
      image: "img1",
    },
    {
      id: "2",
      name: "camp2",
      platform: "plat2",
      landingPage: "LP2",
      image: "img2",
    },
    {
      id: "3",
      name: "camp3",
      platform: "plat3",
      landingPage: "LP3",
      image: "img3",
    },
  ]);

  const [name, setName] = useState("");
  const [platform, setPlatform] = useState("");
  const [landingPage, setLandingPage] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  const [selectedCamp, setSelectedCamp] = useState(null);

  const handleCampClick = (camp) => {
    setSelectedCamp(camp);
    setName(camp.name);
    setPlatform(camp.platform);
    setLandingPage(camp.landingPage);
    setImageUrl(camp.image);
    openModal();
  };

  const handleUpdateCamp = (e) => {
    e.preventDefault();
    if (!selectedCamp) {
      return;
    }

    const updateCamp = {
        id: selectedCamp.id
    }
  };

  const openModal = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleAddCampaign = (e) => {
    e.preventDefault();

    const newCampaign = {
      id: campaignData.length + 1,
      name: name,
      platform: platform,
      landingPage: landingPage,
      image: imageUrl,
    };

    setCampaignData([newCampaign, ...campaignData]);
    setName("");
    setPlatform("");
    setLandingPage("");
    setImageUrl("");

    closeModal();
  };

  return (
    <div className="app-container">
      <button onClick={openModal}>Add Campaign</button>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <form onSubmit={handleAddCampaign}>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </label>
              <label>
                Platform:
                <input
                  type="text"
                  name="platform"
                  value={platform}
                  onChange={(e) => setPlatform(e.target.value)}
                  required
                />
              </label>
              <label>
                Landing page:
                <input
                  type="text"
                  name="landingPage"
                  value={landingPage}
                  onChange={(e) => setLandingPage(e.target.value)}
                  required
                />
              </label>
              <label>
                Image URL:
                <input
                  type="text"
                  name="imageURL"
                  value={imageUrl}
                  onChange={(e) => setImageUrl(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      )}

      <div className="campaign-table">
        <table>
          <thead>
            <tr>
              <th>Campaign Name</th>
              <th>Platform</th>
              <th>Landing Page</th>
              <th>Image</th>
              <th>Edit</th>
              <th>Preview</th>
            </tr>
          </thead>
          <tbody>
            {campaignData.map((camp) => (
              <tr key={camp.id}>
                <td>{camp.name}</td>
                <td>{camp.platform}</td>
                <td>{camp.landingPage}</td>
                <td>{camp.image}</td>
                <td>
                  <button onClick={() => handleCampClick(camp)}>Edit</button>
                </td>
                <td>
                  <button>Preview</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
