import React, { useState } from 'react';
import "./Campaigns.css";

export const Campaigns = () => {
  const [showModal, setShowModal] = useState(false);
  const [campaignData, setCampaignData] = useState({
    name: '',
    platform: '',
    image: null
  });

  const openModal = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCampaignData({
      ...campaignData,
      [name]: value
    });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setCampaignData({
      ...campaignData,
      image: file
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic to handle form submission (e.g., send data to backend)
    console.log(campaignData);
    closeModal();
  };

  return (
    <div className='app-container'>
      <button onClick={openModal}>Add Campaign</button>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>&times;</span>
            <form onSubmit={handleSubmit}>
              <label>
                Name:
                <input type="text" name="name" value={campaignData.name} onChange={handleChange} required />
              </label>
              <label>
                Platform:
                <input type="text" name="platform" value={campaignData.platform} onChange={handleChange} required />
              </label>
              <label>
                Image:
                <input type="file" accept="image/*" onChange={handleImageChange} required />
              </label>
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      )}

      <div className="campaign-table">
        <table>
          <thead>
            <tr>
              <th>Campaign Name</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>campaign title</td>
              <td>campaign desc</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};
